export function attr(...selectors) {
  return { 'data-qa': selectors.join(' ') };
}

function wrap(selectors) {
  const _selectors = selectors.constructor === Array
              ? selectors
              : [selectors];
  return _selectors.map((selector) => `[data-qa="${selector}"]`).join('');
}

export function select(...selectors) {
  return selectors
    .map(wrap)
    .join(' ');
}
