import { expect } from 'chai';
import { attr, select } from './qa';

describe('attr', () => {
  it('returns an object with the data-qa key set', () => {
    expect(attr('example')).to.eql({ 'data-qa': 'example' });
  });

  it('separates multiple arguments with a space', () => {
    expect(attr('example', 'example2')).to.eql({ 'data-qa': 'example example2' });
  });
});

describe('select', () => {
  it('creates a data-qa selector for a single argument', () => {
    expect(select('example')).to.eql('[data-qa="example"]');
  });

  it('creates a selector chain for multiple arguments', () => {
    expect(select('parent', 'child')).to.eql('[data-qa="parent"] [data-qa="child"]');
  });

  it('merge array arguments', () => {
    expect(select(['parent1', 'parent2'], 'child'))
      .to.eql('[data-qa="parent1"][data-qa="parent2"] [data-qa="child"]');
  });
});
