import { expect } from 'chai';
import { createPath } from './url';

describe('createPath', () => {
  it('replaces all instances of the given parameters', () => {
    const result = createPath('example/:id/new/:id', { id: 'test' });
    expect(result).to.equal('example/test/new/test');
  });

  it('replaces multiple parameters', () => {
    const result = createPath('example/:one/new/:two', { one: 'ONE', two: 'TWO' });
    expect(result).to.equal('example/ONE/new/TWO');
  });
});
