import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

import Accordion from './Accordion';

chai.use(chaiEnzyme());

describe('Accordion component', () => {
  const props = {
    accordionHeaderTemplate: <div className="header-template">sds</div>,
    accordionBodyTemplate: <div className="body-template">sss</div>,
    className: 'custom-class',
    isOpen: false,
  };

  it('renders header template', () => {
    const wrapper = shallow(<Accordion {...props} />);
    expect(wrapper.find('.header-template')).to.exist;
  });

  it('sets custom class prop', () => {
    const wrapper = shallow(<Accordion {...props} />);
    expect(wrapper.find('.custom-class')).to.exist;
  });
});
