import React from 'react';
import chai, { expect } from 'chai';
import { mount } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

chai.use(chaiEnzyme());

import Amount from './Amount';

const value = 1234567.12345;
const precision = 3;

describe('Amount component', () => {
  it('renders correct amount with no decimals', () => {
    const wrapper = mount(<Amount value={value} />);
    expect(wrapper).to.have.className('amount');
    expect(wrapper.text()).to.equal('1 234 567');
  });

  it('renders correct amount given precision', () => {
    const wrapper = mount(<Amount value={value} precision={precision} />);
    expect(wrapper).to.have.className('amount');
    expect(wrapper.text()).to.equal('1 234 567.123');
  });
});
