import React, { PropTypes } from 'react';
import { Link } from 'react-router';
import { attr as qa } from '@agct/reacceptance/src/qa';

export const Breadcrumbs = ({ children }) => (
  <ul className="breadcrumbs" {...qa('breadcrumb-list')}>
    {children}
  </ul>
);

Breadcrumbs.propTypes = {
  children: PropTypes.any,
};


export const BreadcrumbsItem = ({ title, url }) => (
  <li className="breadcrumbs__breadcrumb">
    {url &&
      <span>
        <Link to={url} className="breadcrumbs__breadcrumb__link" {...qa('breadcrumb-item')}>
          {title}
        </Link>
      </span>
    }
    {!url && title}
  </li>
);

BreadcrumbsItem.propTypes = {
  title: PropTypes.string.isRequired,
  url: PropTypes.string,
};


export default Breadcrumbs;
