import React from 'react';
import chai, { expect } from 'chai';
import { mount } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

import Breadcrumbs, { BreadcrumbsItem } from 'components/Breadcrumbs/Breadcrumbs';

chai.use(chaiEnzyme());

describe('Breadcrumbs component', () => {
  const props = {
    title: 'title',
    url: 'url',
  };

  it('displays no link if link is not passed in', () => {
    const wrapper = mount(<Breadcrumbs children={<BreadcrumbsItem title={props.title} />} />);
    expect(wrapper.find('.breadcrumbs__breadcrumb')).to.have.length(1);
    expect(wrapper.find('.breadcrumbs__breadcrumb__link')).to.have.length(0);
  });

  it('displays link if link is passed in', () => {
    const wrapper = mount(<Breadcrumbs children={<BreadcrumbsItem title={props.title} url="link" />} />);
    expect(wrapper.find('.breadcrumbs__breadcrumb__link')).to.have.length(1);
  });
});
