import React, { PropTypes } from 'react';
import classnames from 'classnames';
import { attr as qa } from '@agct/reacceptance/src/qa';

const Card = ({ title, subtitle, children }) => {
  const headerClass = classnames({ card__header: title || subtitle });

  return (
    <div className="card" {...qa('card')}>
      <div className={headerClass} {...qa('card-header')}>
        {
          title &&
          <h3 className="card__title" {...qa('card-title')}>
            {title}
          </h3>
        }
        {
          subtitle &&
          <p className="card__subtitle" {...qa('card-subtitle')}>
            {subtitle}
          </p>
        }
      </div>
      <div {...qa('card-body')}>
        {children}
      </div>
    </div>
  );
};

Card.propTypes = {
  title: PropTypes.any,
  subtitle: PropTypes.any,
  children: PropTypes.any,
};

export default Card;
