import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

import Card from './Card';

chai.use(chaiEnzyme());


describe('Card component', () => {
  it('create with title', () => {
    const wrapper = shallow(<Card title="Title" />);
    expect(wrapper.find('.card__header')).to.exist;
    expect(wrapper.find('.card__title')).to.have.text('Title');
  });

  it('create with subtitle', () => {
    const wrapper = shallow(<Card subtitle="Subtitle" />);
    expect(wrapper.find('.card__header')).to.exist;
    expect(wrapper.find('.card__subtitle')).to.have.text('Subtitle');
  });

  it('create with children', () => {
    const wrapper = shallow(<Card><span className="card-children">children</span></Card>);
    expect(wrapper.find('.card__header')).to.not.exist;
    expect(wrapper.find('.card-children')).to.have.text('children');
  });
});
