import React from 'react';
import chai, { expect } from 'chai';
import { mount } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

import CardButton from 'components/CardButton/CardButton';

chai.use(chaiEnzyme());

describe('CardButton component', () => {
  const classList = 'unit-testing-class';
  const children = '<span>Children test</span>';
  const wrapper = mount(<CardButton classes={ classList }>{ children }</CardButton>);

  it('creates card', () => {
    expect(wrapper.find('.card-button')).to.be.present();
  });

  it('accepts additional classes', () => {
    expect(wrapper.find(`.card-button.${classList}`)).to.be.present();
  });

  it('accepts children', () => {
    expect(wrapper.find('.card-button').text()).to.equal(children);
  });
});
