import React from 'react';
import chai, { expect } from 'chai';
import { mount } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

chai.use(chaiEnzyme());

import DropDownItem from './DropDownItem';

describe('DropDown Item', () => {
  const dropdownItem = {
    key: 'item_1',
    label: 'item_label',
  };

  it('renders enabled unselected item correctly', () => {
    const wrapper = mount(
      <DropDownItem
        key={dropdownItem.key}
        item={dropdownItem}
        isSelected={false}
        isDisabled={false}
        onClick={() => {}}
      />);
    expect(wrapper).to.have.className('dropdown__options__option');
    expect(wrapper).to.not.have.className('is-selected');
    expect(wrapper).to.not.have.className('is-disabled');
    expect(wrapper).to.have.text('item_label');
  });

  it('renders disabled unselected item correctly', () => {
    const wrapper = mount(
      <DropDownItem
        key={dropdownItem.key}
        item={dropdownItem}
        isSelected={false}
        isDisabled
        onClick={() => {}}
      />);
    expect(wrapper).to.have.className('dropdown__options__option');
    expect(wrapper).to.not.have.className('is-selected');
    expect(wrapper).to.have.className('is-disabled');
    expect(wrapper).to.have.text('item_label');
  });

  it('renders enabled selected item correctly', () => {
    const wrapper = mount(
      <DropDownItem
        key={dropdownItem.key}
        item={dropdownItem}
        isSelected
        isDisabled={false}
        onClick={() => {}}
      />);
    expect(wrapper).to.have.className('dropdown__options__option');
    expect(wrapper).to.have.className('is-selected');
    expect(wrapper).to.not.have.className('is-disabled');
    expect(wrapper).to.have.text('item_label');
  });
});
