import React, { PropTypes } from 'react';
import classNames from 'classnames';
import { attr as qa } from '@agct/reacceptance/src/qa';
import _ from 'lodash';

const FormField = ({ field, label, prompt, type }) => {
  const showError = field.touched && field.error;

  const baseClass = classNames('input', {
    'is-error': showError, // true/false
  });

  const fieldProps = _.omit({ ...field }, ['touched', 'error']);

  return (
    <div className="input-wrapper">
      <div className={baseClass}>
        {label && <label>{label}</label>}
        <input
          type={type}
          placeholder={prompt}
          {...qa('input')}
          {...fieldProps}
        />
        <div className="input__accent"></div>
      </div>
      {showError && <div className="input__error">{field.error}</div>}
    </div>
  );
};

FormField.propTypes = {
  field: PropTypes.object.isRequired,
  label: PropTypes.string,
  prompt: PropTypes.string,
  type: PropTypes.string,
};

FormField.defaultProps = {
  label: '',
  prompt: '',
  type: 'text',
};


export default FormField;
