import React, { PropTypes } from 'react';

const HighlightedText = ({ searchQuery, textCompare }) => {
  const searchQueryReg = new RegExp(`(${searchQuery})`, 'gi');
  const parts = textCompare.split(searchQueryReg);
  return (
    <span className="highlighted-text">
      {
        parts.map((part, index) => (
            part.toLowerCase() === searchQuery.toLowerCase() ?
              <span className="highlighted-text__highlight" key={index}>{part}</span> :
              <span key={index}>{part}</span>
        ))
      }
    </span>
  );
};

HighlightedText.propTypes = {
  searchQuery: PropTypes.string.isRequired,
  textCompare: PropTypes.string.isRequired,
};

export default HighlightedText;
