import React from 'react';
import chai, { expect } from 'chai';
import { mount } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import HighlightedText from './HighlightedText';

chai.use(chaiEnzyme());

const textCompare = 'Investment Platform Unit Trust';

describe('HighlightedText component', () => {
  it('highlights single search query in results', () => {
    const wrapper = mount(<HighlightedText searchQuery="INVESTMENT p" textCompare={textCompare} />);
    expect(wrapper.find('.highlighted-text__highlight')).to.have.lengthOf(1);
    expect(wrapper.find('.highlighted-text__highlight').text()).to.equal('Investment P');
  });

  it('highlights search results in the middle of the text', () => {
    const wrapper = mount(<HighlightedText searchQuery="Platform" textCompare={textCompare} />);

    expect(wrapper.find('.highlighted-text__highlight')).to.have.lengthOf(1);
    expect(wrapper.find('.highlighted-text__highlight').text()).to.equal('Platform');
    expect(wrapper).to.have.text(textCompare);
  });

  it('highlights multiple search queries in results', () => {
    const wrapper = mount(<HighlightedText searchQuery="t" textCompare={textCompare} />);
    expect(wrapper.find('.highlighted-text__highlight')).to.have.lengthOf(6);
    expect(wrapper).to.have.text(textCompare);
  });

  it('does not highlight if there are no matches', () => {
    const wrapper = mount(<HighlightedText searchQuery="XXX" textCompare={textCompare} />);
    expect(wrapper.find('.highlighted-text__highlight')).to.be.empty;
  });
});
