import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import ArrowRight from './ArrowRight';

chai.use(chaiEnzyme());

describe('ArrowRight component', () => {
  it('create default', () => {
    const wrapper = shallow(<ArrowRight />);
    expect(wrapper.find(select('svg-arrow-right'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<ArrowRight className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
