import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import ChevronDown from './ChevronDown';

chai.use(chaiEnzyme());

describe('ChevronDown component', () => {
  it('create default', () => {
    const wrapper = shallow(<ChevronDown />);
    expect(wrapper.find(select('svg-chevron-down'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<ChevronDown className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
