import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import ChevronLeft from './ChevronLeft';

chai.use(chaiEnzyme());

describe('ChevronLeft component', () => {
  it('create default', () => {
    const wrapper = shallow(<ChevronLeft />);
    expect(wrapper.find(select('svg-chevron-left'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<ChevronLeft className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
