import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import ChevronRight from './ChevronRight';

chai.use(chaiEnzyme());

describe('ChevronRight component', () => {
  it('create default', () => {
    const wrapper = shallow(<ChevronRight />);
    expect(wrapper.find(select('svg-chevron-right'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<ChevronRight className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
