import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import ChevronUp from './ChevronUp';

chai.use(chaiEnzyme());

describe('ChevronUp component', () => {
  it('create default', () => {
    const wrapper = shallow(<ChevronUp />);
    expect(wrapper.find(select('svg-chevron-up'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<ChevronUp className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
