import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';
import Cog from './Cog';

chai.use(chaiEnzyme());

describe('Cog component', () => {
  it('create default', () => {
    const wrapper = shallow(<Cog />);
    expect(wrapper.find(select('svg-cog'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Cog className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
