import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import Delete from './Delete';

chai.use(chaiEnzyme());

describe('Delete component', () => {
  it('create default', () => {
    const wrapper = shallow(<Delete />);
    expect(wrapper.find(select('svg-delete'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Delete className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
