import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';
import Download from './Download';

chai.use(chaiEnzyme());

describe('Download component', () => {
  it('create default', () => {
    const wrapper = shallow(<Download />);
    expect(wrapper.find(select('svg-download'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Download className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
