import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import Edit from './Edit';

chai.use(chaiEnzyme());

describe('Edit component', () => {
  it('create default', () => {
    const wrapper = shallow(<Edit />);
    expect(wrapper.find(select('svg-edit'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Edit className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
