import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';
import MenuHamburger from './MenuHamburger';

chai.use(chaiEnzyme());

describe('MenuHamburger component', () => {
  it('create default', () => {
    const wrapper = shallow(<MenuHamburger />);
    expect(wrapper.find(select('svg-menu-hamburger'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<MenuHamburger className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
