import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';
import Pdf from './Pdf';

chai.use(chaiEnzyme());

describe('Pdf component', () => {
  it('create default', () => {
    const wrapper = shallow(<Pdf />);
    expect(wrapper.find(select('svg-pdf'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Pdf className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
