import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';
import Pencil from './Pencil';

chai.use(chaiEnzyme());

describe('Pencil component', () => {
  it('create default', () => {
    const wrapper = shallow(<Pencil />);
    expect(wrapper.find(select('svg-pencil'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Pencil className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
