import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import Reocurance from './Reocurance';

chai.use(chaiEnzyme());

describe('Reocurance component', () => {
  it('create default', () => {
    const wrapper = shallow(<Reocurance />);
    expect(wrapper.find(select('svg-reocurance'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Reocurance className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
