import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import Search from './Search';

chai.use(chaiEnzyme());

describe('Search component', () => {
  it('create default', () => {
    const wrapper = shallow(<Search />);
    expect(wrapper.find(select('svg-search'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Search className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
