import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';
import { select } from '@agct/reacceptance/src/qa';

import Tick from './Tick';

chai.use(chaiEnzyme());

describe('Tick component', () => {
  it('create default', () => {
    const wrapper = shallow(<Tick />);
    expect(wrapper.find(select('svg-tick'))).to.be.present();
  });

  it('handles className prop', () => {
    const wrapper = shallow(<Tick className="customClass" />);
    expect(wrapper.find('.customClass')).to.be.present();
  });
});
