import React, { PropTypes } from 'react';
import classnames from 'classnames';

export const LOADING_SMALL = 'small';
export const LOADING_LARGE = 'large';

const Loading = ({ size }) => {
  const baseClass = classnames('loading-spinner', {
    'loading-spinner--small': size === LOADING_SMALL,
    'loading-spinner--large': size === LOADING_LARGE,
  });

  return (
    <svg className={baseClass}
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 66 66"
    >
      <circle className="loading-spinner__path"
        cx="33"
        cy="33"
        r="30"
        style={{
          fill: 'none',
          strokeWidth: 6,
          strokeLinecap: 'round',
        }}
      ></circle>
    </svg>
  );
};

Loading.propTypes = {
  size: PropTypes.string,
};

export default Loading;
