import React from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

import Loading, { LOADING_SMALL, LOADING_LARGE } from './Loading';

chai.use(chaiEnzyme());


describe('Loading component', () => {
  it('create default', () => {
    const wrapper = shallow(<Loading />);
    expect(wrapper.find('.loading-spinner')).to.be.present();
  });

  it('create small', () => {
    const wrapper = shallow(<Loading size={LOADING_SMALL} />);
    expect(wrapper.find('.loading-spinner--small')).to.be.present();
  });

  it('create large', () => {
    const wrapper = shallow(<Loading size={LOADING_LARGE} />);
    expect(wrapper.find('.loading-spinner--large')).to.be.present();
  });
});
