import React, { PropTypes } from 'react';
import { formatMoneyWithSymbol, formatMoneyWithCode } from '../../utils/formatting/money';

const Money = ({ amount, currencyCode, useSymbol }) => {
  const format = (useSymbol) ? formatMoneyWithSymbol : formatMoneyWithCode;
  const money = format(amount, currencyCode);
  return (
    <span className="money">{money}</span>
  );
};

Money.propTypes = {
  amount: PropTypes.number.isRequired,
  currencyCode: PropTypes.string.isRequired,
  useSymbol: PropTypes.bool,
};

Money.defaultProps = {
  useSymbol: true,
};

export default Money;
