import React, { PropTypes } from 'react';
import { formatMoneyDestructured } from '../../utils/formatting/money';

const DecorativeMoney = ({ amount, currencyCode, useSymbol = true, invertNumber = false }) => {
  const money = formatMoneyDestructured(amount, currencyCode);
  const symbol = useSymbol ? money.symbol : money.isoCode;
  const { isNegative } = money;
  const invertNegative = isNegative && invertNumber;
  const units = invertNegative ? money.units.replace('-', ' ') : money.units;

  return (
    <span className="money">
      {invertNegative && <span className="money__prefix">-</span>}
      <span className="money__symbol">{symbol}</span>
      <span className="money__units">{units}</span>
      {money.cents && <span className="money__decimal">{money.decimal}</span>}
      {money.cents && <span className="money__cents">{money.cents}</span>}
    </span>
  );
};

DecorativeMoney.propTypes = {
  amount: PropTypes.number.isRequired,
  currencyCode: PropTypes.string.isRequired,
  useSymbol: PropTypes.bool,
  invertNumber: PropTypes.bool,
};

export default DecorativeMoney;
