import React, { PropTypes } from 'react';
import classNames from 'classnames';
import uiWrapper from '../UIWrapper/UIWrapper';


const NavDrawer = ({ ui, children }) => {
  const baseClass = classNames('nav-drawer', { 'is-open': ui.isOpen });

  return (
    <div className={baseClass}>
      <button className="nav-drawer__toggle" onClick={ui.toggle}>
        <i className="agi agi-menu-hamburger"></i> Menu
      </button>

      <nav className="nav-drawer__wrapper">
        {children}
      </nav>
    </div>
  );
};

NavDrawer.propTypes = {
  ui: PropTypes.object.isRequired,
  children: PropTypes.any,
};

export default uiWrapper(NavDrawer);
