import React from 'react';
import { mount, shallow } from 'enzyme';
import chai, { expect } from 'chai';
import chaiEnzyme from 'chai-enzyme';

chai.use(chaiEnzyme());

import NavHeader from './NavHeader';

describe('NavHeader component', () => {
  it('should render', () => {
    const wrapper = mount(<NavHeader />);
    expect(wrapper).to.have.className('nav-bar');
    expect(wrapper.childAt(0)).to.have.className('nav-bar__brand');
    expect(wrapper.childAt(1)).to.have.className('nav-bar__items');
  });

  it('should render children when passed in', () => {
    const wrapper = shallow(
      <NavHeader>
        <div className="nav-bar__items__item">Item 1</div>
        <div className="nav-bar__items__item">Item 2</div>
      </NavHeader>
      );
    expect(wrapper.contains(<div className="nav-bar__items__item">Item 1</div>)).to.equal(true);
    expect(wrapper.contains(<div className="nav-bar__items__item">Item 2</div>)).to.equal(true);
  });
});
