import React, { PropTypes } from 'react';
import chai, { expect } from 'chai';
import { shallow } from 'enzyme';
import chaiEnzyme from 'chai-enzyme';

chai.use(chaiEnzyme());

import Pill from './Pill';

const pillContent = 'In Progress';

describe('Pill component', () => {
  it('renders correctly', () => {
    let wrapper = shallow(<Pill content={pillContent} />);
    expect(wrapper).to.have.className('pill');
    expect(wrapper).to.have.className('red');
    expect(wrapper.text()).to.equal(pillContent);

    wrapper = shallow(<Pill content={pillContent} className={'ag__secondary'} />);
    expect(wrapper).to.have.className('pill');
    expect(wrapper).to.have.className('ag__secondary');
    expect(wrapper.text()).to.equal(pillContent);
  });

  it('should have prop types defined', () => {
    expect(Pill.propTypes.content).to.equal(PropTypes.any.isRequired);
    expect(Pill.propTypes.className).to.equal(PropTypes.string);
  });
});
