import React from 'react';
import chai, { expect } from 'chai';
import chaiEnzyme from 'chai-enzyme';
import { shallow } from 'enzyme';
import RouteNotFound from './RouteNotFound';

chai.use(chaiEnzyme());

describe('RouteNotFound Component', () => {
  it('should render with text', () => {
    const wrapper = shallow(<RouteNotFound />);
    expect(wrapper.find('.route-not-found-container')).have.length(1);
    expect(wrapper.find('h1')).to.have.text('Sorry, we cannot find that page');
    expect(wrapper.find('p')).to.have.text(
      'This page is not available as it might be temporarily down or may have moved permanently to a new web address.'
    );
  });
});
