import { SET_TOKEN, CLEAR_TOKEN } from './actions';


const initialState = {
  token: null,
};

const session = (state = initialState, action) => {
  switch (action.type) {
    case SET_TOKEN:
      return {
        ...state,
        token: action.token,
      };

    case CLEAR_TOKEN:
      return {
        ...state,
        token: null,
      };

    default:
      return state;
  }
};

export default session;
