import accounting from 'accounting';


export const defaultNumberOptions = {
  precision: 0,
  thousand: ' ',
  decimal: '.',
};

export const formatNumber = (number, options = {}) => accounting.formatNumber(number, {
  ...defaultNumberOptions,
  ...options,
});

export const formatDecimal = (number, options = {}) => formatNumber(number, {
  precision: 2,
  ...options,
});
