import isEmpty from './isEmpty';

/**
  isBlank();                // true
  isBlank(null);            // true
  isBlank(undefined);       // true
  isBlank('');              // true
  isBlank([]);              // true
  isBlank('\n\t');          // true
  isBlank('  ');            // true
  isBlank({});              // false
  isBlank('\n\t Hello');    // false
  isBlank('Hello world');   // false
  isBlank([1,2,3]);         // false
*/

const isBlank = (obj) => (
  isEmpty(obj) || (typeof obj === 'string' && obj.match(/\S/) === null)
);

export default isBlank;
