import { expect } from 'chai';
import isSouthAfricanId from './isSouthAfricanId';

const correctIdAsNumber = 8001015009087;
const correctIdAsString = '8001015009087';
const incorrectId = '8001015009080';
const incorrectIdLength1 = '800101500908';
const incorrectIdLength2 = '80010150090870';

describe('isSouthAfricanId', () => {
  it('passes on incorrect id as number', () => {
    expect(isSouthAfricanId(correctIdAsNumber)).to.be.true;
  });

  it('passes on incorrect id as string', () => {
    expect(isSouthAfricanId(correctIdAsString)).to.be.true;
  });

  it('fails on undefined id', () => {
    expect(isSouthAfricanId()).to.be.false;
  });

  it('fails on incorrect id', () => {
    expect(isSouthAfricanId(incorrectId)).to.be.false;
  });

  it('fails on incorrect id length', () => {
    expect(isSouthAfricanId(incorrectIdLength1)).to.be.false;
    expect(isSouthAfricanId(incorrectIdLength2)).to.be.false;
  });
});
